import React, { useState, useEffect } from 'react';
import api from './services/api';

import "./styles.css";

function App() {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    api.get('repositories').then(response => {
      setProjects(response.data);
    })
  }, []);

  async function handleAddRepository() {
    const response = await api.post('repositories', {
      title: `Projeto de sistem ${Date.now()}`,
      url: `https://github.com/Rocketseat/repository${Date.now()}`,
      techs: ["REACT", "REACT-NATIVE", "MYSQL"]

    });
    const project = response.data;
    setProjects([...projects, project]);
  }

  async function handleRemoveRepository(id) {
    const repositoryIndex = projects.findIndex(repository => repository.id === id);

    const response = await api.delete(`/repositories/${id}`)

    projects.splice(repositoryIndex, 1);

    setProjects([...projects]);
  }
  async function handleLikeRepository(id) {
    const response = await api.post(`repositories/${id}/like`);
    const project = response.data;
    setProjects([project]);
  }

  return (
    <div>
      <ul data-testid="repository-list">
        {projects.map(project =>
          <li key={project.id}>{project.title} - {project.likes} like(s)
            <button onClick={() => handleRemoveRepository(project.id)}>Remove</button>
            <button onClick={() => handleLikeRepository(project.id)} class='like'>Like</button></li>
        )
        }
      </ul>

      <button onClick={handleAddRepository}>Add</button>
    </div>
  );
}

export default App;
